package by.belstu.khramykh;import java.util.Objects;public class WrapperString {    private String stringWrapper;    int a = 35;    public WrapperString(String string) {        stringWrapper = string;    }    public WrapperString() {    }    public String getStringWrapper() {        return stringWrapper;    }    public void setStringWrapper(String stringWrapper) {        this.stringWrapper = stringWrapper;    }    @Override    public boolean equals(Object o) {        if (this == o) return true;        if (o == null || getClass() != o.getClass()) return false;        WrapperString that = (WrapperString) o;        return Objects.equals(stringWrapper, that.stringWrapper);    }    @Override    public int hashCode() {        return Objects.hash(stringWrapper);    }    @Override    public String toString() {        return stringWrapper;    }    public String replace (char oldChar, char newChar) {        return stringWrapper.replace(oldChar, newChar);    }}