package by.belstu.Employees;public class Engineer extends Employees {    public Engineer(String firstName, String secondName, double salary) {        this.firstName = firstName;        this.secondName = secondName;        this.salary = salary;        this.type = Engineer.class.toString();    }    public Engineer() {}    public static String getType(){        return Developer.class.toString();    }    public String show() {        return "---------------------------------------------------------\n" +                "Engineer: \n" +                firstName + " " +                secondName +                "\nЗарплата - " + salary + " $\n" +                "\n---------------------------------------------------------";    }}