package by.belstu.Employees;public class SystemAdministrator extends Employees{    public SystemAdministrator(String firstName, String secondName, double salary) {        this.firstName = firstName;        this.secondName = secondName;        this.salary = salary;        this.type = SystemAdministrator.class.toString();    }    public SystemAdministrator(){}    public static String getType(){        return Developer.class.toString();    }    public String show() {        return "---------------------------------------------------------\n"+                "System Administrator: \n" +                firstName + " " +                secondName+                "\nЗарплата - " + salary + " $\n" +                "\n---------------------------------------------------------";    }}