package by.belstu;public class Developer extends Employee {    private Position position;    private double salary = 0;    public enum Position {        Junior,        Middle,        Senior    }    public Developer(String firstName, String secondName, Position position, double salary) {        this.firstName = firstName;        this.secondName = secondName;        this.position = position;        this.salary = salary;    }    public Position getPosition() {        return position;    }    public String show() {        return  "\n---------------------------------------------------------\n"+                "Developer: \n" +                firstName + " " +                secondName  +                "\nУровень :" + position+                "\nЗарплата - " + salary + " $\n" +                "\n---------------------------------------------------------";    }}