package by.belstu;public class Engineer extends Employee {    public Engineer(String firstName, String secondName, double salary) {        this.firstName = firstName;        this.secondName = secondName;        this.salary = salary;    }    public String show() {        return "\n---------------------------------------------------------\n"+                "Engineer: \n" +                firstName + " " +                secondName+                "\nЗарплата - " + salary + " $\n" +                "\n---------------------------------------------------------";    }}