package by.belstu.Employees;public class Developer extends Employees{    public enum Position {        Junior,        Middle,        Senior    }    public Developer(){}    public Developer(String firstName, String secondName, Position position, double salary) {        this.firstName = firstName;        this.secondName = secondName;        this.position = position;        this.salary = salary;        this.type = Developer.class.toString();    }    @Override    public String show() {        return  "---------------------------------------------------------\n"+                "Developer: \n" +                firstName + " " +                secondName  +                "\nУровень - " + position+                "\nЗарплата: " + salary + " $\n" +                "\n---------------------------------------------------------";    }    public static String getType(){        return Developer.class.toString();    }    public Position getPosition() {        return position;    }}