package app.entities;public class InhabitantType {    private int id=0;    private String name;    private String language;    public InhabitantType(){}    public InhabitantType(String name, String language) {        this.name = name;        this.language = language;    }    public InhabitantType(int id,String name, String language) {        this.id = id;        this.name = name;        this.language = language;    }    public int getId() {        return id;    }    public void setId(int id) {        this.id = id;    }    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }    public String getLanguage() {        return language;    }    public void setLanguage(String language) {        this.language = language;    }    @Override    public String toString() {        return  "Type of Inhabitant - " + name +                "\tlanguage - " + language;    }}