import java.io.BufferedReader;import java.io.IOException;import java.io.InputStreamReader;import java.net.InetAddress;import java.net.MalformedURLException;import java.net.URL;import java.net.UnknownHostException;public class NetClass {    InetAddress currentIP = null;    public NetClass() throws UnknownHostException {        setCurrentIP();    }    public void setCurrentIP() throws UnknownHostException {        currentIP = InetAddress.getLocalHost();    }    public InetAddress getCurrentIP() {        return currentIP;    }    public String getAddressByName(String host) throws UnknownHostException {        InetAddress tmp = null;        tmp = InetAddress.getByName(host);        return tmp.getHostAddress();    }    public String getHtml(String urlName){        URL url = null;        String content = "";        try{            url = new URL(urlName);        } catch (MalformedURLException e) {            e.printStackTrace();        }        if(url == null)            throw new RuntimeException();        try(BufferedReader bufferedReader = new BufferedReader(new InputStreamReader(url.openStream()))){            while(bufferedReader.readLine() != null)                content += bufferedReader.readLine();        } catch (IOException e) {            e.printStackTrace();        }        return content;    }}