import java.io.*;import java.net.DatagramPacket;import java.net.DatagramSocket;import java.net.SocketException;class Recipient {    public static void main(String[] args) {        DatagramSocket ds = null;        try {            ds = new DatagramSocket(1050);        } catch (SocketException e) {            e.printStackTrace();        }        while (true) {            DatagramPacket pack = new DatagramPacket(new byte[1024], 1024);            try {                ds.receive(pack);            } catch (IOException e) {                e.printStackTrace();            }            System.out.println(pack.getData().length);            System.out.println(new String(pack.getData()));        }    }}