package app.Model;import app.Entities.User;import java.util.ArrayList;import java.util.List;public class Model {    private static Model instance = new Model();    private List<User> model;    public static Model getInstance(){        return instance;    }    private Model(){        model = new ArrayList<User>();    }    public void addUser(User user){        model.add(user);    }    public List<User> getUsers() {        return model;    }}