package app.Servlets;import javax.servlet.RequestDispatcher;import javax.servlet.ServletException;import javax.servlet.http.HttpServlet;import javax.servlet.http.HttpServletRequest;import javax.servlet.http.HttpServletResponse;import java.io.IOException;import java.net.InetAddress;import java.text.SimpleDateFormat;import java.util.Date;import java.util.Enumeration;public class TimeServlet extends HttpServlet{    @Override    protected void doGet(HttpServletRequest req, HttpServletResponse resp) throws ServletException, IOException {        req.setCharacterEncoding("UTF-8");        Date date = new Date();        SimpleDateFormat formatForDateNow = new SimpleDateFormat("E yyyy.MM.dd 'и время' hh:mm:ss a zzz");        String dateString = "Текущая дата " + formatForDateNow.format(date);        String address = req.getRemoteAddr();        String name = InetAddress.getLocalHost().getHostName();        String protocol = req.getProtocol();        String headers = "";        Enumeration headerNames = req.getHeaderNames();        while(headerNames.hasMoreElements()){            headers+=(String)headerNames.nextElement()+"\n";        }        req.setAttribute("headers",headers);        req.setAttribute("time", dateString);        req.setAttribute("p", protocol);        req.setAttribute("address",address);        req.setAttribute("name",name);        RequestDispatcher requestDispatcher = req.getRequestDispatcher("views/time.jsp");        requestDispatcher.forward(req, resp);    }    @Override    protected void doPost(HttpServletRequest req, HttpServletResponse resp) throws ServletException, IOException {        req.setCharacterEncoding("UTF-8");    }}