package Servlets;import DBHandler.DbHandler;import Entities.User;import javax.servlet.RequestDispatcher;import javax.servlet.ServletException;import javax.servlet.http.Cookie;import javax.servlet.http.HttpServlet;import javax.servlet.http.HttpServletRequest;import javax.servlet.http.HttpServletResponse;import java.io.IOException;import java.sql.SQLException;import java.text.SimpleDateFormat;import java.util.ArrayList;import java.util.Collection;import java.util.Date;public class UsersServlet extends HttpServlet {    protected void doPost(HttpServletRequest request, HttpServletResponse response) throws ServletException, IOException {        RequestDispatcher requestDispatcher = request.getRequestDispatcher("views/users.jsp");        requestDispatcher.forward(request,response);    }    protected void doGet(HttpServletRequest request, HttpServletResponse response) throws ServletException, IOException {        DbHandler dataBaseHandler = new DbHandler();        try {            Collection<User> users = dataBaseHandler.getAllUsers();            request.setAttribute("users", users);        } catch (SQLException e) {            e.printStackTrace();        }        RequestDispatcher requestDispatcher = request.getRequestDispatcher("views/users.jsp");        requestDispatcher.forward(request, response);    }}