package app.Entities;public class User {    int id;    String firstName = "";    String secondName = "";    String mail = "";    String password = "";    public User(String firstName, String secondName, String mail, String password) {        this.firstName = firstName;        this.secondName = secondName;        this.mail = mail;        this.password = password;    }    public User(){        this.firstName = "empty";        this.secondName = "empty";        this.mail = "empty";        this.password = "empty";    }    public User(int id, String firstName, String secondName, String mail, String password) {        this.id = id;        this.firstName = firstName;        this.secondName = secondName;        this.mail = mail;        this.password = password;    }    public String getFirstName() {        return firstName;    }    public void setFirstName(String firstName) {        this.firstName = firstName;    }    public String getSecondName() {        return secondName;    }    public void setSecondName(String secondName) {        this.secondName = secondName;    }    public String getMail() {        return mail;    }    public void setMail(String mail) {        this.mail = mail;    }    public String getPassword() {        return password;    }    public void setPassword(String password) {        this.password = password;    }}