package app.Servlets;import app.Entities.User;import app.db.DataBaseHandler;import javax.servlet.RequestDispatcher;import javax.servlet.ServletException;import javax.servlet.http.HttpServlet;import javax.servlet.http.HttpServletRequest;import javax.servlet.http.HttpServletResponse;import java.io.IOException;import java.sql.SQLException;public class RegServlet extends HttpServlet {    protected void doPost(HttpServletRequest request, HttpServletResponse response) throws ServletException, IOException {        request.setCharacterEncoding("UTF-8");        DataBaseHandler dataBaseHandler = new DataBaseHandler();        String mail = request.getParameter("mail");        String firstName = request.getParameter("firstName");        String secondName = request.getParameter("secondName");        String password = request.getParameter("password");        String privilege = request.getParameter("privilege");        User tmp = new User(firstName,secondName,mail,password,privilege);        try {            if(dataBaseHandler.registration(tmp))                request.setAttribute("done","Всё норм");            else                request.setAttribute("done","Такой уже есть)");        } catch (SQLException e) {            System.out.println("SQL Exception");        }        RequestDispatcher requestDispatcher = request.getRequestDispatcher("views/registration.jsp");        requestDispatcher.forward(request,response);    }    protected void doGet(HttpServletRequest request, HttpServletResponse response) throws ServletException, IOException {        RequestDispatcher requestDispatcher = request.getRequestDispatcher("views/registration.jsp");        requestDispatcher.forward(request, response);    }}