package app.Tags;import javax.servlet.jsp.JspException;import javax.servlet.jsp.JspWriter;import javax.servlet.jsp.tagext.TagSupport;import java.io.IOException;public class LoginAndReg extends TagSupport {    @Override    public int doStartTag() throws JspException {        String html = "<button>Registration</button>\n" +                "<button>Login</button>";        try {            JspWriter out = pageContext.getOut();            out.write(html);        } catch (IOException e) {            throw new JspException(e.getMessage());        }        return SKIP_BODY;    }    @Override    public int doEndTag() throws JspException {        return EVAL_PAGE;    }}