package by.belstu;public class SystemAdministrator extends Employee {    public SystemAdministrator(String firstName, String secondName, double salary) {        this.firstName = firstName;        this.secondName = secondName;        this.salary = salary;    }    public String show() {        return "\n---------------------------------------------------------\n"+                "System Administrator: \n" +                firstName + " " +                secondName+                "\nЗарплата - " + salary + " $\n" +                "\n---------------------------------------------------------";    }}