package by.belstu;import javax.xml.transform.*;import javax.xml.transform.stream.StreamResult;import javax.xml.transform.stream.StreamSource;import java.io.File;import java.io.FileNotFoundException;import java.io.FileOutputStream;import java.io.OutputStream;public class TransToHtml {    public static void toHTML(String xsltPath,String xmlPath,String htmlPath){        try{            TransformerFactory transformerFactory = TransformerFactory.newInstance();            Transformer transformer = transformerFactory.newTransformer(new StreamSource(new File("src/index.xsl")));            StreamResult streamResult = new StreamResult(new File("src/index.html"));            StreamSource streamSource = new StreamSource(new File("src/JAXB_and_SAX.xml"));            transformer.transform(streamSource,streamResult);            System.out.println("html сформирован!");        }catch(Exception e){            System.out.println("Ошибка при формировании html!");            e.printStackTrace();        }    }}