package by.belstu.XML;import by.belstu.ItCompany;import com.alibaba.fastjson.JSON;import com.google.gson.Gson;import com.google.gson.GsonBuilder;import java.io.*;import java.nio.file.Files;import java.nio.file.Paths;public class Serialize {    public static void datSerialize(Object tmp, String path){        try(ObjectOutputStream oos = new ObjectOutputStream(new FileOutputStream(path))) {            oos.writeObject(tmp);            System.out.println("Сериализация в dat выполнена!");        }catch(Exception e){            e.printStackTrace();        }    }    public static Object datDeserialize(String path){        try(ObjectInputStream ois = new ObjectInputStream(new FileInputStream(path))){            Object tmp = ois.readObject();            return tmp;        }catch(Exception ex){            System.out.println(ex.getMessage());        }        return null;    }    public static void JSONSerialize(Object tmp, String path) throws IOException {        try(FileWriter writer = new FileWriter(path)){            Gson gson = new Gson();            gson.toJson(tmp, writer);            System.out.println("Сериализация в JSON выполнена!");        }catch (Exception e){            e.printStackTrace();        }    }    public static ItCompany JSONDeSerialize(String path) throws IOException {        ItCompany tmp;        Gson gson = new GsonBuilder().create();        String jsonData = new String (Files.readAllBytes(Paths.get(path)));        tmp = gson.fromJson(jsonData, ItCompany.class);        System.out.println("Дессериализация из JSON выполнена!");        return tmp;    }}