package by.belstu.Employees;public class Developer extends Employees{    public enum Position {        Junior,        Middle,        Senior    }    public Developer(){}    public Developer(String firstName, String secondName, Position position, double salary) {        if(firstName != null && secondName != null && position != null && salary != 0){            this.firstName = firstName;            this.secondName = secondName;            this.position = position;            this.salary = salary;            this.type = Developer.class.toString();        }        else{            System.out.println("Передан пустой параметр(ы)!");        }    }    @Override    public String show() {        return  "---------------------------------------------------------\n"+                "Developer: \n" +                firstName + " " +                secondName  +                "\nУровень - " + position+                "\nЗарплата: " + salary + " $\n" +                "\n---------------------------------------------------------";    }    public static String getType(){        return Developer.class.toString();    }    public Position getPosition() {        return position;    }}