package by.belstu.Employees;import javax.xml.bind.annotation.XmlElement;import javax.xml.bind.annotation.XmlType;import java.io.Serializable;@XmlType(propOrder = {"firstName","secondName","type","salary","position"}, name="employee")public class Employees implements Serializable{    @XmlElement    String firstName;    @XmlElement    String secondName;    @XmlElement    String type;    @XmlElement    double salary = 0;    @XmlElement    Developer.Position position;    public Employees(){}    public String getFirstName() {        return firstName;    }    public String getSecondName() {        return secondName;    }    public double getSalary() {        return salary;    }    public String show() {        return "---------------------------------------------------------\n" +                "Employee: \n" +                firstName + " " +                secondName +                "\nЗарплата - " + salary + " $\n" +                "\n---------------------------------------------------------";    }}