package app.DBConfig;import com.mysql.fabric.jdbc.FabricMySQLDriver;import java.sql.*;public class DataBaseHandler extends DBConfig {    Connection dbConnection;    public Connection getConnection() throws ClassNotFoundException, SQLException{        String connectionString = "jdbc:mysql://"+dbHost+":"+dbPort+"/"+dbName;        try{            Driver driver = new FabricMySQLDriver();            DriverManager.registerDriver(driver);            dbConnection = DriverManager.getConnection(connectionString,dbUser,dbPassword);        }catch (SQLException e){            e.printStackTrace();        }        return dbConnection;    }}