package app.entities;public class Region {    private int id = 0;    private String name;    private double area;    public int getWeatherId() {        return weatherId;    }    public void setWeatherId(int weatherId) {        this.weatherId = weatherId;    }    public int getInhabitantTypeId() {        return inhabitantTypeId;    }    public void setInhabitantTypeId(int inhabitantTypeId) {        this.inhabitantTypeId = inhabitantTypeId;    }    private int weatherId;    private int inhabitantTypeId;    private Weather weather;    private InhabitantType inhabitantType;    public Region(){}    public Region(String name, double area) {        this.name = name;        this.area = area;    }    public int getId() {        return id;    }    public void setId(int id) {        this.id = id;    }    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }    public double getArea() {        return area;    }    public void setArea(double area) {        this.area = area;    }    public Weather getWeather() {        return weather;    }    public void setWeather(Weather weather) {        this.weather = weather;    }    public InhabitantType getInhabitantType() {        return inhabitantType;    }    public void setInhabitantType(InhabitantType inhabitantType) {        this.inhabitantType = inhabitantType;    }    @Override    public String toString() {        return  "\nRegion - " + name +                "\nArea - " + area +                "\nWeather - " + weather.toString() +                "\nInhabitantType - " + inhabitantType.toString();    }}