package app.entities;import java.sql.Date;public class Weather {    private int id;    private Date date;    private int temperature;    private int precipitation;    public Weather(){}    public Weather(Date date, int temperature, int precipitation) {        this.date = date;        this.temperature = temperature;        this.precipitation = precipitation;    }    public Weather(int id, Date date, int temperature, int precipitation) {        this.id = id;        this.date = date;        this.temperature = temperature;        this.precipitation = precipitation;    }    public int getId() {        return id;    }    public void setId(int id) {        this.id = id;    }    public Date getDate() {        return date;    }    public void setDate(Date date) {        this.date = date;    }    public int getTemperature() {        return temperature;    }    public void setTemperature(int temperature) {        this.temperature = temperature;    }    public int getPrecipitation() {        return precipitation;    }    public void setPrecipitation(int precipitation) {        this.precipitation = precipitation;    }    @Override    public String toString() {        return  "date - " + date +                "\ttemperature - " + temperature +                "\tprecipitation - " + precipitation +" %";    }}